// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./Raffle.sol";

/**
 * @title RaffleFactory
 * @notice A factory contract that creates single-winner raffles in `MultiRaffle`.
 *         Users pay a creationFee to spawn a new raffle, and each raffle must
 *         have a ticketSize >= minTicketPrice.
 */
contract RaffleFactory is Ownable, Pausable, ReentrancyGuard {
    
    /// @notice The underlying raffle engine (MultiRaffle).
    MultiRaffle public multiRaffle;

    /// @notice Fee to pay when creating a new raffle (in wei).
    uint256 public creationFee;

    /// @notice Minimum ticket price any raffle must use.
    uint256 public minTicketPrice;

    /// @notice Total fees collected so far (in wei).
    uint256 public totalFeesCollected;

    /// @dev raffleId => who created it
    mapping(uint256 => address) public raffleCreator;

    /// @dev user => array of raffleIds they created
    mapping(address => uint256[]) public rafflesCreatedBy;

    uint256 public constant SEED_COUNT = 6;

    event FactoryRaffleCreated(
        address indexed creator,
        uint256 indexed raffleId,
        string raffleName
    );

    event FactoryRaffleRestarted(uint256 indexed oldId, uint256 indexed newId);
    event CreationFeeUpdated(uint256 oldFee, uint256 newFee);
    event MinTicketPriceUpdated(uint256 oldPrice, uint256 newPrice);
  


    /**
     * @param _multiRaffle    The deployed MultiRaffle contract
     * @param _creationFee    The fee (in wei) for creating a new raffle
     * @param _minTicketPrice The minimum ticket price (in wei) any raffle must have
     */
    constructor(
        MultiRaffle _multiRaffle,
        uint256 _creationFee,
        uint256 _minTicketPrice
    ) {
        require(address(_multiRaffle) != address(0), "Invalid MultiRaffle");
        multiRaffle    = _multiRaffle;
        creationFee    = _creationFee;
        minTicketPrice = _minTicketPrice;
    }

    /**
     * @notice Create a new raffle by paying the `creationFee`.
     *         Must have `_settings.ticketSize >= minTicketPrice`.
     * @param _settings       The RaffleSettings struct for single-winner
     * @param _raffleName     Raffle metadata name
     * @param _raffleImage    Raffle image URI (ipfs:// or http)
     * @param _raffleDesc     Raffle description
     * @param _raffleDuration Duration in seconds
     */
    function createNewRaffle(
        MultiRaffle.RaffleSettings memory _settings,
        string memory _raffleName,
        string memory _raffleImage,
        string memory _raffleDesc,
        uint256 _raffleDuration
    )
        external
        payable
        whenNotPaused
        nonReentrant
    {
        // 1) Must pay exactly creationFee
        require(msg.value == creationFee, "Must pay exact creation fee");

        // 2) Enforce minimum ticket price
        require(_settings.ticketSize >= minTicketPrice, "Ticket < minTicketPrice");

        // 3) Overwrite raffleDuration in the struct
        _settings.raffleDuration = _raffleDuration;

        // 4) Call MultiRaffle to create the raffle (only works if factory is set in MultiRaffle)
        uint256 newRaffleId = multiRaffle.createRaffle(
            _settings,
            _raffleName,
            _raffleImage,
            _raffleDesc
        );

        // 5) Track who created it
        raffleCreator[newRaffleId] = msg.sender;
        rafflesCreatedBy[msg.sender].push(newRaffleId);

        // 6) Account for fees
        totalFeesCollected += msg.value;

        // 7) Emit event
        emit FactoryRaffleCreated(msg.sender, newRaffleId, _raffleName);
    }

    /**
     * @notice Withdraw all accumulated creation fees.
     */
    function withdrawFees() external onlyOwner nonReentrant {
        uint256 bal = address(this).balance;
        require(bal > 0, "No fees to withdraw");

        totalFeesCollected = 0;

        (bool ok, ) = payable(owner()).call{ value: bal }("");
        require(ok, "Withdraw failed");
    }

    /**
     * @notice Update the creationFee (owner only).
     * @param newFee The new creation fee in wei.
     */
    function setCreationFee(uint256 newFee) external onlyOwner {
        uint256 old = creationFee;
        creationFee = newFee;
        emit CreationFeeUpdated(old, newFee);
    }

    /**
     * @notice Update the minTicketPrice (owner only).
     * @param newPrice The new minimum ticket price in wei.
     */
    function setMinTicketPrice(uint256 newPrice) external onlyOwner {
        uint256 old = minTicketPrice;
        minTicketPrice = newPrice;
        emit MinTicketPriceUpdated(old, newPrice);
    }

    /**
     * @notice Pause the factory (owner only).
     *         No new raffles can be created when paused.
     */
    function pauseFactory() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the factory (owner only).
     */
    function unpauseFactory() external onlyOwner {
        _unpause();
    }

     /** 
     * @notice Restart only a migration‑seeded raffle once it’s finished
     */
     function restartRaffle(uint256 oldId, uint256 newDuration)
    external
    payable
    whenNotPaused
    nonReentrant
    onlyOwner
{
    require(msg.value == creationFee, "Pay exact creation fee");
    require(oldId < SEED_COUNT, "Only initial raffles can be restarted");

    // 1) check old round finished
    (, , , , , , bool ended, bool winnerPicked, , ) = multiRaffle.getRaffleData(oldId);
    require(ended && winnerPicked, "Round not finished");

    // 2) pull only the six immutable settings from the 15‑field struct
    (
      string memory name,
      string memory image,
      string memory desc,
      uint256 ticketSize,
      uint256 numberRange,
      uint256 houseTake,
      , , , , , , , , 
    ) = multiRaffle.raffles(oldId);

    // 3) build new settings
    MultiRaffle.RaffleSettings memory s = MultiRaffle.RaffleSettings({
        ticketSize:     ticketSize,
        numberRange:    numberRange,
        houseTake:      houseTake,
        raffleDuration: newDuration
    });

    // 4) spawn the clone
    uint256 newId = multiRaffle.createRaffle(s, name, image, desc);

    // 5) record creator & fees
    raffleCreator[newId] = msg.sender;
    rafflesCreatedBy[msg.sender].push(newId);
    totalFeesCollected += msg.value;

    emit FactoryRaffleCreated(msg.sender, newId, name);
    emit FactoryRaffleRestarted(oldId, newId);
}


    // --------------------------------------------------------
    // VIEW FUNCTIONS
    // --------------------------------------------------------

    /**
     * @notice Return how many raffles exist in the MultiRaffle engine.
     */
    function getRaffleCount() external view returns (uint256) {
        return multiRaffle.getRafflesLength();
    }

    /**
     * @notice Return all raffleIds created by a user.
     */
    function getRafflesCreatedBy(address user) external view returns (uint256[] memory) {
        return rafflesCreatedBy[user];
    }

    /**
     * @notice Return the current ETH balance of this factory (the creation fees).
     */
    function getFactoryBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

